import React, { useState } from 'react';

function OpenAIQuestion(props) {
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");

  const askQuestion = async () => {
    const response = await fetch("http://localhost:5000/ask", {  // Call your Flask API
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ question }),
    });

    const data = await response.json();
    setAnswer(data.answer);  // Display the OpenAI's response in the chat
  };

  return (
    <div>
      <input
        type="text"
        value={question}
        onChange={(e) => setQuestion(e.target.value)}
        placeholder="Escribe tu pregunta"
      />
      <button onClick={askQuestion}>Preguntar</button>
      {answer && <div><strong>Respuesta: </strong>{answer}</div>}
    </div>
  );
}

export default OpenAIQuestion;
