import { createChatBotMessage } from 'react-chatbot-kit';
import OpenAIQuestion from './components/OpenAIQuestion/OpenAIQuestion';

const config = {
  botName: "LearningBot",
  initialMessages: [
    createChatBotMessage("Hola, estoy aquí para ayudarte. ¿Tienes alguna pregunta?", {
      widget: "openaiQuestion",  // Use the OpenAI question widget
    }),
  ],

  widgets: [
    {
      widgetName: "openaiQuestion",
      widgetFunc: (props) => <OpenAIQuestion {...props} />,  // Handle OpenAI interactions
    },
  ],
};

export default config;
