import { createChatBotMessage } from 'react-chatbot-kit';
import OpenAIQuestion from './components/OpenAIQuestion/OpenAIQuestion';  // Your OpenAI question component

const config = {
  botName: "LearningBot",
  initialMessages: [
    createChatBotMessage("Hola, estoy aquí para ayudarte. ¿Qué te gustaría preguntar?", {
      widget: "openaiQuestion",  // Trigger the openaiQuestion widget
    }),
  ],

  widgets: [
    {
      widgetName: "openaiQuestion",
      widgetFunc: (props) => <OpenAIQuestion {...props} />,
    },
  ],
};

export default config;
